#include "stdlib/math/base/special/absf.h"
#include "stdlib/math/base/special/sqrtf.h"
#include "stdlib/math/base/special/abs2f.h"
#include "stdlib/math/base/special/copysignf.h"
#include "stdlib/blas/base/shared.h"


/**
* Constructs a Givens plane rotation using alternative indexing semantics.
*
* @param a - rotational elimination parameter
* @param b - rotational elimination parameter
* @param Out - output array
* @param strideOut - stride length for `Out`
* @param offsetOut - starting index for `Out`
*/
void API_SUFFIX( c_srotg_assign )( float a, float b, float* Out, const CBLAS_INT strideOut, const CBLAS_INT offsetOut ) {
	float scale;
	float sign;
	float aa;
	float ab;
	float r;
	float c;
	float s;
	float z;

	aa = stdlib_base_absf( a );
	ab = stdlib_base_absf( b );
	if ( aa > ab ) {
		sign = stdlib_base_copysignf( 1.0, a );
	} else {
		sign = stdlib_base_copysignf( 1.0, b );
	}
	scale = aa + ab;
	if ( scale == 0.0 ) {
		c = 1.0;
		s = 0.0;
		r = 0.0;
		z = 0.0;
	} else {
		r = scale * stdlib_base_sqrtf( stdlib_base_abs2f( a/scale ) + stdlib_base_abs2f( b/scale ) );
		r = r * sign;
		c = a / r;
		s = b / r;
		z = 1.0;
		if ( aa > ab ) {
			z = s;
		} else if ( c != 0.0 ) {
			z = 1.0 / c;
		}
	}
	a = r;
	b = z;
	Out[ offsetOut ] = a;
	Out[ offsetOut + strideOut ] = b;
	Out[ offsetOut + ( 2 * strideOut ) ] = c;
	Out[ offsetOut + ( 3 * strideOut ) ] = s;
	return;
}
