#include "stdlib/stats/base/dists/logistic/logcdf.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/constants/float64/ninf.h"
#include "stdlib/math/base/special/log1p.h"
#include "stdlib/math/base/special/exp.h"


/**
* Evaluates the logarithm of the cumulative distribution function (CDF) for a logistic distribution with location parameter `mu` and scale parameter `s` at a value `x`.
*
* @param {number} x - input value
* @param {number} mu - location parameter
* @param {NonNegativeNumber} s - scale parameter
* @returns {number} evaluated logCDF
*
* @example
* double y = stdlib_base_dists_logistic_logcdf( 2.0, 0.0, 1.0 );
* // returns ~-0.127
*
* @example
* double y = stdlib_base_dists_logistic_logcdf( 5.0, 10.0, 3.0 );
* // returns ~-1.84
*
* @example
* double y = stdlib_base_dists_logistic_logcdf( 2.0, 0.0, NaN );
* // returns NaN
*
* @example
* double y = stdlib_base_dists_logistic_logcdf( 2, NaN, 1.0 );
* // returns NaN
*
* @example
* double y = stdlib_base_dists_logistic_logcdf( NaN, 0.0, 1.0 );
* // returns NaN
*/
double stdlib_base_dists_logistic_logcdf( const double x, const double mu, const double s ) {
	double z;
	if (
		stdlib_base_is_nan( x ) ||
		stdlib_base_is_nan( mu ) ||
		stdlib_base_is_nan( s ) ||
		s < 0.0
	) {
		return 0.0 / 0.0; // NaN
	}
	if ( s == 0.0 ) {
		return ( x < mu ) ? STDLIB_CONSTANT_FLOAT64_NINF : 0.0;
	}
	z = -( ( x - mu ) / s );
	if ( z <= 18.0 ) {
		return -stdlib_base_log1p( stdlib_base_exp(z) );
	}
	if ( z > 33.3 ) {
		return -z;
	}
	// Case: 18.0 < z <= 33.3
	return -( x + stdlib_base_exp( -z ) );
}
